INSERT INTO `boards` (`id`,`subject`,`content`,`created`,`modified`)
VALUES
	(1,'10/13 IED歐洲設計學院[台北.高雄 面試]- 11月7.8.9日，請預約面試','<p>\r\n	<span class=\"style7\"><img alt=\"\" src=\"/~Dars/amigo/admin//files/1265187803.jpg\" style=\"width: 265px; height: 180px; float: left; margin: 5px;\" /><span style=\"color: rgb(165, 42, 42);\"><span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">歐教展[台北展區]<br />\r\n	IED歐洲設計學院將於台北11月7.8日參加歐洲教育展<br />\r\n	歡迎對學校課程有興趣的朋友前來[世貿二館]了解<br />\r\n	時間為下午13:00-18:00<br />\r\n	並有現場面試,與代表一對一面談</span></span></span></span><span style=\"color: rgb(165, 42, 42);\"><span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\"><br />\r\n	</span></span></span></p>\r\n<p class=\"style8\">\r\n	<span style=\"color: rgb(255, 140, 0);\"><span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">歐教展[高雄展區]<br />\r\n	今年第一次歐教展於[高雄]舉辦,歡迎位於南部朋友<br />\r\n	於11月9日15:00- 19:00 至國賓飯店詢問課程<br />\r\n	現場也有面試,請把握機會</span></span></span></p>\r\n<p class=\"style6\">\r\n	<span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">歡迎與我們聯繫. 02 2721 3452</span></span></p>\r\n','2010-02-03 17:04:26','2010-02-03 17:09:09'),
	(2,'7/2 義大利Polimoda 2009年獎學金訊息發佈—AUDI汽車提供','<p>\r\n	<img alt=\"\" src=\"/~Dars/amigo/admin//files/1265188019.jpg\" style=\"width: 265px; height: 265px; margin: 5px; float: left;\" /><span style=\"font-size: 14px;\">AUDI-佛羅倫斯分部 提供獎學金 5000歐元給Polimoda <a href=\"http://www.amigoinfo.com.tw/polimodamanagerment.html\">時尚視覺傳達碩士</a> 課程的學生。 </span></p>\r\n<p class=\"style6\">\r\n	<span style=\"font-size: 14px;\">獎學金科系：時尚視覺傳達　Master in Fashion Visual Communication<br />\r\n	參加資格：大學畢業或準畢業生<br />\r\n	報名截止日：2009年9月。<br />\r\n	開　課　日：2009年10月。<br />\r\n	提供獎金　：5,000歐元。<br />\r\n	申請文件　：申請表履歷與讀書計劃。<br />\r\n	考試內容　：性向測驗，廣告提案，申請動機訪談。<br />\r\n	考試日期　：2009年9月16日上午10:00 於Polimoda學校內.<br />\r\n	Ｐolimoda將由教師們組成評估委員會,由以上內容甄選出得獎者。<br />\r\n	<span class=\"style2\"><strong><br />\r\n	</strong></span> 更相關的獎學金詳細規則，煩請洽詢 02 2721-3452 ~Emi</span></p>\r\n','2010-02-03 17:08:19','2010-02-03 17:08:19'),
	(3,'6/30 IED 2009國際學生獎學金訊息發佈','<p class=\"style6\">\r\n	<img alt=\"\" src=\"/~Dars/amigo/admin//files/1265188314.jpg\" style=\"width: 265px; height: 166px; margin: 5px; float: left;\" /><span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\"><a href=\"http://www.amigoinfo.com.tw/iedschool.html\">IED歐洲 設計學院</a> 提供2009年-2010年碩士班30個名額的獎學金申請。</span></span></p>\r\n<p class=\"style6\">\r\n	<span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">比賽主題 &ndash; 設計一個更美好的世界.　（參賽者藉著作品反應或提倡主題）<br />\r\n	申請截止日期：2009年9月10日<br />\r\n	提供名額：30位.每個科系各一名。包含以下：<br />\r\n	　　　　　15個全額獎學金(不包含註冊費).<br />\r\n	　　　　　15個半額獎學金(不包含註冊費).<br />\r\n	申請條件：20-40歲.相關科系的學士學位或同等學歷.<br />\r\n	申請文件：參賽表格+履歷+自傳+獎學金讀書計劃+主題作品集+大學的文憑+護照影本+作品題目+想法的描述(30行內)+至少10張圖片或影片或手稿+ 每個檔案要1000pixels以上+可加60分鐘以內的影像+可加成品的照片. (作品不可大於10M)<br />\r\n	服裝設計要有簡短描+15件手稿. 註明材質.顏色.成品的照片.(作品要10M以上)<br />\r\n	語言要求：與授課語言相符的語言.( 電話面試 )<br />\r\n	內容要求：需本人作品. 作品不得是抄襲.暴力.攻擊.人性尊嚴等.</span></span></p>\r\n<p class=\"style6\">\r\n	<span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">下列為不符合資格者：<br />\r\n	1.已獲得IED其他課程獎學金者.<br />\r\n	2.IED的員工與顧問.<br />\r\n	3.參賽者選擇一個以上的課程申請獎學金.</span></span></p>\r\n<p class=\"style6\">\r\n	<span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\">截止日期：2009年9月15日前.(以寄件日為準)<br />\r\n	公佈日期：2009年10月10日.<br />\r\n	備註：作品無法退還.IED有權利應用.<br />\r\n	評審：由IED四個科別的評審選出<br />\r\n	30個獎學金科系：<br />\r\n	&nbsp;1.&nbsp;&nbsp; <a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Milan/Fashion%20Communication%20and%20Stylist%20%28English%20Language%29/info.html?courseId=MRA425I\">Master in Fashion Communication and Stylist</a> (English language) Milan<a id=\"OLE_LINK4\" name=\"OLE_LINK4\"></a> ( 50% )<br />\r\n	2.&nbsp; &nbsp;<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Milan/Fashion%20Marketing%20and%20Retail%20Management%20%28English%20language%29/info.html?courseId=MRA003I\">Master in Fashion Marketing and Retail Management </a>(English language) Milan&nbsp; ( 50% )<br />\r\n	3.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Milan/Fashion%20and%20Textile%20Design%20%28English%20language%29/info.html?courseId=MRA001I\">Master in Fashion and Textile Design</a> (English language) Milan&nbsp; (100% )<br />\r\n	4.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Rome/Luxury%20Marketing%20Management%20%28English%20language%29/info.html?courseId=MRB432E\">Master in Luxury Marketing Management</a> (English language) Rome ( 50% )<br />\r\n	5.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Master-Courses/Visual-Arts/Madrid/Motion%20graphics%20Design/info.html?courseId=VME614S\">Master in Motion Graphics Design</a> (Spanish language), Madrid &nbsp;( 50% )<br />\r\n	6.<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Barcelona/Fashion%20Management%20and%20Retail/info.html?courseId=MRD944S\"> &nbsp;Master in Fashion Management and Retail</a> (Spanish language), Barcelona ( 50% )<br />\r\n	7.&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Milan/Interior%20Design%20%28English%20language%29/info.html?courseId=DRA005I\">Master in Interior Design</a> (English language) Milan ( 50% )<br />\r\n	8.&nbsp; &nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Milan/Interior%20Design%20%28English%20language%29/info.html?courseId=DRA005I\">Master in Light Design</a> (English language) Milan ( 100% )<br />\r\n	9.&nbsp; &nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Milan/Packaging%20Design%20%28English%20language%29/info.html?courseId=DRA007I\">Master in Packaging Design</a> (English language) Milan ( 100% )<br />\r\n	10.<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Milan/Product%20Design%20%28English%20language%29/info.html?courseId=DRA004I\"> &nbsp;Master in Product Design</a> (English language) Milan ( 50% )<br />\r\n	11.<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Turin/Advanced%20Design%20for%20Transportation%20%28english%20language%29/info.html?courseId=DRC928E\"> &nbsp;Master in Advanced Design for Transportation</a> (English language) Turin ( 50% )<br />\r\n	12. <u><a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Turin/Master%20of%20Arts%20in%20Transportation%20Design%20-%20Work%20Experience%20%28english%20language%29/info.html?courseId=DRC001I\">&nbsp;Master of Arts in Transportation Design - Work Experience</a></u> (English language) Torino ( 100% )<br />\r\n	13. <a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Venice/Yacht%20Design%20%28English%20language%29/info.html?courseId=DRG541E\">&nbsp;Master in Yacht Design </a>(English language) Venice ( 100% )<br />\r\n	14. &nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Madrid/European%20Design%20Labs%20-%20Product%20Design%20%28English%20language%29/info.html?courseId=DRE468S\">Master in European Design Labs</a>: Product Design (English language), Madrid ( 100% )<br />\r\n	15. &nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Barcelona/Design%20Management%20%28english%20language%29/info.html?courseId=CRD755E\">Master in Design Management</a> (English language), Barcelona ( 100% )<br />\r\n	16. &nbsp;<a href=\"http://www.ied.edu/Courses/Communication/Master-Courses/Milan/Brand%20Management%20and%20Advertising%20Communication%20%28English%20language%29/info.html?courseId=CRA636E\">Master inBrand Management and Advertising Communicatio</a>n (English language) Milan ( 100% )<br />\r\n	17. &nbsp;<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Rome/Sustainable%20Urban%20Design/info.html?courseId=DMB1118I\">Master in Urban design </a>(English language), Rome ( 100% )<br />\r\n	18. <a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Rome/Contemporary%20Landscape%20-%20Visual%20Research%20and%20Design/info.html?courseId=DMB1117I\">&nbsp;Master in&nbsp; Landscape design </a>(English language), Rome &nbsp;( 100% )<br />\r\n	19. &nbsp;<a href=\"http://www.ied.edu/Courses/Master-Courses/Design/Turin/Industrial%20Design%20for%20Sport%20-%20Extreme%20Design%20%28english%20language%29/info.html?courseId=DRC246I\">Master in Industrial design for extreme sport </a>(English language), Turin ( 100% )<br />\r\n	20. &nbsp;Master in&nbsp; Sustainibility for Architecture (English language), Turin ( 100% )<br />\r\n	21. &nbsp;Master in Photography and digital imaging&nbsp; (English language), Venice ( 50% )<br />\r\n	22 &nbsp;Master in<a href=\"http://www.ied.edu/Courses/Design/Master-Courses/Barcelona/Interior%20Design%20for%20Commercial%20Spaces%20%28english%20language%29/info.html?courseId=DMD417E\"> Interior design for commercial spaces</a> (English language), Barcelona ( 50% )<br />\r\n	23.&nbsp;&nbsp;Master in Arts Managmement&nbsp; (English language), Venice ( 50% )<br />\r\n	24.&nbsp;&nbsp;&nbsp;MBA in Luxury and Fashion Management&nbsp; (English language),&nbsp; Paris-Milan ( 100% )<br />\r\n	25.&nbsp;&nbsp;&nbsp;Master in&nbsp; Filmmaker (Italian language), Venice ( 50% )<br />\r\n	26.&nbsp;&nbsp;&nbsp;Master in&nbsp; <a href=\"http://www.ied.edu/Courses/Visual-Arts/Master-Courses/Barcelona/Graphic%20Design%20-%20Strategic%20Creativity%20%28local%20language%29/info.html?courseId=VAD945S\">Graphic Design</a> (Spanish language), Barcelona ( 100% )<br />\r\n	27.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Communication/Master-Courses/Barcelona/Strategic%20and%20Creative%20Management%20of%20Integral%20Advertising%20%28local%20language%29/info.html?courseId=CMD756S\">Master in Advertising (Spanish language), </a>Barcelona ( 50% )<br />\r\n	28.&nbsp;&nbsp;&nbsp;Master in Institutional Communication&nbsp; (Spanish language),&nbsp; Barcelona ( 50% )<br />\r\n	29.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Barcelona/Fashion%20Marketing%20and%20Communication%20%28local%20language%29/info.html?courseId=MMD288S\">Master in Fashion Marketing and Communication</a> (Spanish language),Barcelona ( 50% )<br />\r\n	30.&nbsp;&nbsp;&nbsp;<a href=\"http://www.ied.edu/Courses/Fashion/Master-Courses/Barcelona/Shoes%20and%20Accessories%20Design%20%28local%20language%29/info.html?courseId=MMD289S\">Master in Shoes and Accessories Design</a>, (Spanish language), Barcelona ( 50% )</span></span></p>\r\n<p>\r\n	<span style=\"font-family: verdana,geneva,sans-serif;\"><span style=\"font-size: 14px;\"><span class=\"style6\">更相關的獎學金詳細規則，煩請洽詢 02 2721-3452 ~Emi</span><br />\r\n	</span></span></p>\r\n','2010-02-03 17:14:26','2010-02-03 17:14:26');
